,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'CREATE OR REPLACE EDITIONABLE PACKAGE "EBA_DEMO_REORDER" is',
'    procedure move_source_to_target(p_emp_source_id number, p_emp_target_id number);',
'end;',
'/',
'',
'',
'CREATE OR REPLACE EDITIONABLE PACKAGE BODY "EBA_DEMO_REORDER" is',
'    procedure move_source_to_target(p_emp_source_id number, p_emp_target_id number) is',
'      l_seq integer := 0;',
'    begin',
'        for j in (select lu.emp_id, ',
'                         lu.sort_sequence                           ',
'                   from eba_demo_reorder_emp_lineup lu',
'                   left join eba_demo_reorder_emp e on e.id = lu.emp_id',
'                   order by lu.sort_sequence nulls last, e.created) loop',
'                   -- if we see the source, then skip it for now',
'                   if j.emp_id = p_emp_source_id then',
'                      continue;',
'                   elsif j.emp_id = p_emp_target_id then',
'                      l_seq := l_seq + 1;',
'                      -- current row is the target, so insert source here',
'                      update eba_demo_reorder_emp_lineup',
'                      set sort_sequence = l_seq',
'                      where emp_id = p_emp_source_id;',
'                      -- bump the sort sequence',
'                   end if;',
'                   l_seq := l_seq + 1;',
'                   if j.sort_sequence is null or l_seq != j.sort_sequence then',
'                      update eba_demo_reorder_emp_lineup',
'                      set sort_sequence = l_seq',
'                      where emp_id = j.emp_id;',
'                   end if; ',
'        end loop;',
'    end;           ',
'end;',
'/',
'',
''))
);
