prompt --application/deployment/install/install_json_duality_companion_package
begin
wwv_flow_imp_shared.create_install_script(
 p_id=>wwv_flow_imp.id(22347506596948964)
,p_install_id=>wwv_flow_imp.id(20866007560419587)
,p_name=>'JSON_DUALITY_COMPANION Package'
,p_sequence=>30
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace package json_duality_companion is',
'    c_json_temp_table        constant varchar2(42) := ''JSON_DUALITY_COMPANION_TEMP_TABLE'';',
'',
'    type t_json_table_to_migrate is record (',
'        id              number,',
'        json_table_name varchar2(255),',
'        dualv_name      varchar2(255));',
'',
'    type t_json_tables_to_migrate is table of t_json_table_to_migrate;',
'',
'    function get_table_names_from_ddl(p_ddl clob) ',
'        return apex_t_varchar2;',
'',
'    procedure clear_json_tables_to_migrate;',
'',
'    function json_tables_to_migrate_count',
'    return number;',
'',
'    function add_json_table_to_migrate(',
'        p_json_table_name in varchar2,',
'        p_dualv_name      in varchar2)',
'        return               number;',
'',
'    procedure update_json_table_to_migrate(',
'        p_json_table_to_migrate in t_json_table_to_migrate);',
'',
'    procedure remove_json_table_to_migrate(',
'        p_json_table_to_migrate in t_json_table_to_migrate);',
'',
'    function count_json_tables_to_migrate',
'        return number;',
'',
'    function json_tables_to_migrate',
'        return t_json_tables_to_migrate',
'    pipelined;',
'',
'    function json_duality_migrator_config(',
'        p_use_flex_fields         in boolean  default true,',
'        p_output_format           in varchar2 default ''executable'',',
'        p_updatable_duality_views in boolean  default true)',
'        return           clob;',
'',
'    procedure create_temp_json_collection_for(',
'        p_json_document in clob);',
'',
'    procedure drop_temp_json_collection;',
'',
'    function duality_view_exists(',
'        p_dualv_name in varchar2)',
'        return          boolean;',
'',
'    function existing_duality_views_to_migrate',
'        return varchar2;',
'',
'    function is_valid_json(',
'        p_json_document in clob)',
'        return             boolean;',
'',
'    procedure insert_doc_into_duality_view(',
'        p_json_document in clob,',
'        p_dualv_name    in varchar2);',
'',
'    procedure drop_duality_view(',
'        p_dualv_name in varchar2,',
'        p_drop_related_tables in boolean default false);',
'',
'    procedure drop_tables_from_ddl(',
'        p_ddl clob);',
'',
'    procedure drop_duality_views_to_migrate(',
'         p_drop_related_tables in boolean default false);',
'',
'    procedure import_duality_views_to_migrate;',
'',
'    function diagram_text_for_dualv(',
'        p_dualv_name in varchar2)',
'        return          clob;     ',
'        ',
'end json_duality_companion;',
'/',
'create or replace package body json_duality_companion is',
'',
'    c_json_tables_to_migrate  constant varchar2(50) := ''JSON_DUALITY_COMPANION.JSON_TABLES_TO_MIGRATE'';',
'    c_newline                 constant varchar2(2)  := chr(13)||chr(10);',
'    c_mermaid_erd             constant varchar2(9)  := ''erDiagram'';',
'    c_one_to_many             constant varchar2(8)  := '' ||--|{ '';',
'    c_open_curly_brace        constant varchar2(2)  := '' {'';',
'    c_closed_curly_brace      constant varchar2(1)  := ''}'';',
'    c_empty_relationship_name constant varchar2(2)  :=  ''""'';',
'    c_colon_separator         constant varchar2(3)  :=  '' : '';',
'    c_spaces                  constant varchar2(2)  := ''  '';',
'    ',
'    cursor q_json_tables_to_migrate is',
'        select seq_id as id,',
'               c001   as json_table_name,',
'               c002   as dualv_name',
'          from apex_collections',
'         where collection_name = c_json_tables_to_migrate',
'         order by seq_id;',
'',
'    cursor q_count_json_tables_to_migrate is',
'        select count(*) as num_tables',
'          from apex_collections',
'         where collection_name = c_json_tables_to_migrate',
'         order by seq_id;',
'',
'    function get_table_names_from_ddl(p_ddl clob) ',
'        return apex_t_varchar2',
'    is',
'        l_tables apex_t_varchar2 := apex_t_varchar2();',
'        l_idx    pls_integer;',
'    begin',
'        for j in (select column_value as line from apex_string.split(p_ddl,chr(10))) loop',
'            if j.line like ''EXECUTE IMMEDIATE ''''CREATE TABLE %('' then',
'                l_idx := instr(j.line, ''CREATE TABLE '') + length(''CREATE TABLE '');',
'                j.line := substr(j.line, l_idx);',
'                l_idx := instr(j.line, ''('') - 1;',
'                apex_string.push(l_tables,trim(substr(j.line, 1, l_idx)));',
'            end if;',
'        end loop; ',
'    return l_tables;',
'    end get_table_names_from_ddl;',
'',
'    -- Concatentate a varchar2 to a clob as fast as possible.',
'    -- Quicker than only a dbms_lob.append',
'    -- Thank you to Mathieu Meeuwissen for the suggested approach',
'    procedure vc_append(p_clob in out nocopy clob,',
'                        p_vc   in out nocopy varchar2,',
'                        p_text               varchar2)',
'      is',
'      begin',
'        p_vc := p_vc || p_text;',
'      exception',
'        when value_error',
'        then',
'          if p_clob is null',
'          then',
'            p_clob := p_vc;',
'          else',
'            dbms_lob.append(p_clob,p_vc);',
'          end if;',
'          p_vc := p_text;',
'      end vc_append;',
'',
'    -- Format table title for Mermaid JS ER diagram',
'    function format_title(',
'                p_table_name       in varchar2) ',
'                return                varchar2',
'    is',
'      l_table_name varchar2(200) := p_table_name;',
'    begin',
'        return ''"''||l_table_name||''"'';',
'    end format_title;',
'',
'    function count_json_tables_to_migrate',
'    return number',
'    is',
'    begin',
'        for j in q_count_json_tables_to_migrate loop',
'            return j.num_tables;',
'        end loop;',
'    end;',
'',
'    function json_tables_to_migrate',
'    return t_json_tables_to_migrate',
'    pipelined is',
'    begin',
'        for j in q_json_tables_to_migrate loop',
'            pipe row(t_json_table_to_migrate(',
'                        j.id,',
'                        j.json_table_name,',
'                        j.dualv_name));',
'        end loop;',
'    end;',
'',
'    function json_tables_to_migrate_count',
'    return number',
'    is',
'    begin',
'        return apex_collection.collection_member_count(c_json_tables_to_migrate);',
'    end json_tables_to_migrate_count;',
'',
'    procedure clear_json_tables_to_migrate',
'    is',
'    begin',
'        apex_collection.create_or_truncate_collection(c_json_tables_to_migrate);',
'    end clear_json_tables_to_migrate;',
'',
'    function add_json_table_to_migrate(',
'        p_json_table_name in varchar2,',
'        p_dualv_name      in varchar2)',
'        return               number',
'    is',
'    begin',
'        if not apex_collection.collection_exists(c_json_tables_to_migrate) then',
'            clear_json_tables_to_migrate;',
'        end if;',
'        return apex_collection.add_member(',
'                p_collection_name => c_json_tables_to_migrate,',
'                p_c001            => p_json_table_name,',
'                p_c002            => p_dualv_name);',
'    end add_json_table_to_migrate;',
'',
'    procedure update_json_table_to_migrate(',
'        p_json_table_to_migrate t_json_table_to_migrate)',
'    is',
'    begin',
'        apex_collection.update_member(',
'            p_collection_name => c_json_tables_to_migrate,',
'            p_seq             => p_json_table_to_migrate.id,',
'            p_c001            => p_json_table_to_migrate.json_table_name,',
'            p_c002            => p_json_table_to_migrate.dualv_name);',
'    end update_json_table_to_migrate;',
'',
'    procedure remove_json_table_to_migrate(',
'        p_json_table_to_migrate t_json_table_to_migrate)',
'    is',
'    begin',
'        apex_collection.delete_member(',
'            p_collection_name => c_json_tables_to_migrate,',
'            p_seq             => p_json_table_to_migrate.id);',
'    end remove_json_table_to_migrate;',
'',
'    function json_duality_migrator_config(',
'        p_use_flex_fields         in boolean  default true,',
'        p_output_format           in varchar2 default ''executable'',',
'        p_updatable_duality_views in boolean  default true)',
'        return           clob',
'    is',
'        l_ret         clob;',
'        l_config      json_object_t := json_object_t();',
'',
'        function json_table_names_array',
'        return json_array_t',
'        is',
'            l_ret json_array_t := json_array_t();',
'        begin',
'            for j in q_json_tables_to_migrate loop',
'                l_ret.append(j.json_table_name);',
'            end loop;',
'            return l_ret;',
'        end json_table_names_array;',
'',
'        function dualv_names_array',
'        return json_array_t',
'        is',
'            l_ret json_array_t := json_array_t();',
'        begin',
'            for j in q_json_tables_to_migrate loop',
'                l_ret.append(j.dualv_name);',
'            end loop;',
'            return l_ret;',
'        end dualv_names_array;',
'    begin',
'        l_config.put(''tableNames'',json_table_names_array());',
'        l_config.put(''viewNames'',dualv_names_array());',
'        if not p_use_flex_fields then ',
'            l_config.put(''useFlexFields'',p_use_flex_fields);',
'        end if;',
'        if not p_updatable_duality_views then ',
'            l_config.put(''updatability'',p_updatable_duality_views);',
'        end if;',
'        if p_output_format != ''executable'' then',
'            l_config.put(''outputFormat'',p_output_format);',
'        end if;',
'        l_ret := l_config.to_clob;',
'        select json_serialize(l_ret returning clob pretty)',
'          into l_ret;',
'        return l_ret;',
'    end json_duality_migrator_config;',
'',
'    procedure create_temp_json_collection_for(',
'        p_json_document in clob)',
'    is',
'        pragma autonomous_transaction;',
'        l_json json := json(p_json_document extended);',
'    begin',
'        execute immediate apex_string.format(''drop table if exists %s'',c_json_temp_table);',
'        execute immediate apex_string.format(''create json collection table %s'',c_json_temp_table);',
'        execute immediate apex_string.format(''insert into %s(data) values (:data)'',c_json_temp_table)',
'        using l_json;',
'        commit;',
'    end create_temp_json_collection_for;',
'',
'    procedure drop_temp_json_collection',
'    is',
'        pragma autonomous_transaction;',
'    begin',
'        execute immediate apex_string.format(''drop table if exists %s'',c_json_temp_table);',
'        commit;',
'    end drop_temp_json_collection;',
'',
'    function is_valid_json(',
'        p_json_document in clob)',
'        return             boolean',
'    is',
'        l_json json;',
'    begin',
'        l_json := json(p_json_document extended);',
'        return true;',
'    exception',
'        when others then',
'            return false;',
'    end is_valid_json;',
'',
'    procedure insert_doc_into_duality_view(',
'        p_json_document in clob,',
'        p_dualv_name    in varchar2)',
'    is',
'    begin',
'        execute immediate apex_string.format(''insert into %s(data) values(:data)'',p_dualv_name)',
'        using p_json_document;',
'    end insert_doc_into_duality_view;',
'',
'    procedure drop_duality_view(',
'        p_dualv_name in varchar2,',
'        p_drop_related_tables in boolean default false)',
'    is',
'        pragma autonomous_transaction;',
'        l_tables_to_drop apex_t_varchar2 := apex_t_varchar2();',
'    begin',
'        if p_drop_related_tables then',
'            for j in (select referenced_name as table_to_drop',
'                        from user_dependencies',
'                       where name = p_dualv_name',
'                         and type = ''VIEW'' ',
'                         and referenced_type = ''TABLE''',
'                         and referenced_name not like ''BIN$%'') loop',
'                ',
'                apex_string.push(l_tables_to_drop,j.table_to_drop);',
'            end loop;',
'            apex_debug.info(''Will drop %s tables related to duality view %s'',l_tables_to_drop.count,p_dualv_name);',
'        end if;',
'        execute immediate apex_string.format(''drop view if exists %s'',p_dualv_name);',
'        apex_debug.info(''Dropped duality view %s'',p_dualv_name);',
'        for j in 1..l_tables_to_drop.count loop',
'            execute immediate apex_string.format(''drop table if exists %s cascade constraints'',l_tables_to_drop(j));',
'            apex_debug.info(''Dropped dependent table %s for duality view %s'',l_tables_to_drop(j),p_dualv_name);',
'        end loop;',
'        commit;',
'    end drop_duality_view;',
'    ',
'    procedure drop_duality_views_to_migrate(',
'         p_drop_related_tables in boolean default false)',
'    is',
'    begin ',
'        for j in q_json_tables_to_migrate loop',
'            drop_duality_view(j.dualv_name,p_drop_related_tables);',
'        end loop;      ',
'    end drop_duality_views_to_migrate;',
'',
'    -----------------------------------------------------------------------------  ',
'    -- Generate Mermaid JS ER diagram text for all tables related to duality view',
'    ----------------------------------------------------------------------------- ',
'    function diagram_text_for_dualv(',
'    p_dualv_name in varchar2)',
'    return          clob',
'    is',
'        l_ret               clob;',
'        l_ret_vc            varchar2(32767);',
'',
'        procedure p(p_text varchar2) is',
'        begin',
'            vc_append(l_ret,l_ret_vc,p_text || c_newline);',
'        end;',
'',
'        function final_clob return clob is',
'        begin',
'            return l_ret || l_ret_vc;',
'        end;',
'',
'    begin',
'        p(c_mermaid_erd);',
'        -- add top level tables having no foreign key of their own',
'        for t in (select ut.table_name as lone_table_name',
'                    from user_tables ut',
'                   where (ut.table_name in (select referenced_name as table_to_drop',
'                                              from user_dependencies',
'                                             where name = p_dualv_name',
'                                               and type = ''VIEW'' ',
'                                               and referenced_type = ''TABLE''))',
'                   minus',
'                  select distinct table_name from user_constraints uc',
'                   where uc.constraint_type = ''R''',
'                     and (uc.table_name in (select referenced_name as table_to_drop',
'                                              from user_dependencies',
'                                             where name = p_dualv_name',
'                                               and type = ''VIEW'' ',
'                                               and referenced_type = ''TABLE''))) loop',
'            p(format_title(t.lone_table_name));',
'        end loop;',
'        -- add tables that include a foreign key',
'        for t in (select fk.table_name as many_table, pk.table_name as one_table',
'                    from user_constraints fk',
'                    left outer join user_constraints pk on pk.constraint_name = fk.r_constraint_name',
'                    where fk.constraint_type = ''R''',
'                    and (fk.table_name in (select referenced_name as table_to_drop',
'                                              from user_dependencies',
'                                             where name = p_dualv_name',
'                                               and type = ''VIEW'' ',
'                                               and referenced_type = ''TABLE''))',
'                    and (pk.table_name in (select referenced_name as table_to_drop',
'                                              from user_dependencies',
'                                             where name = p_dualv_name',
'                                               and type = ''VIEW'' ',
'                                               and referenced_type = ''TABLE''))) loop',
'            p(format_title(t.one_table)',
'                || c_one_to_many',
'                || format_title(t.many_table)',
'                || c_colon_separator',
'                || c_empty_relationship_name);',
'        end loop;',
'        -- If we''re including columns, then generate the table descriptions',
'        -- to accompany the relationships above.',
'        for t in (select table_name',
'                   from user_tables',
'                   where (table_name in (select referenced_name as table_to_drop',
'                                              from user_dependencies',
'                                             where name = p_dualv_name',
'                                               and type = ''VIEW'' ',
'                                               and referenced_type = ''TABLE''))) loop',
'            p(format_title(t.table_name)||c_open_curly_brace);',
'            for col in (select c.column_name,',
'                     case when to_char(c.data_length) is not null and (c.data_type = ''NUMBER'')',
'                          then ''number'' || case when c.data_precision is not null then ''_''||to_char(c.data_precision) || ''_'' || nvl(c.data_scale,0) end',
'                          when c.data_type in (''BLOB'',''CLOB'',''DATE'')',
'                          then lower(c.data_type)',
'                          when c.data_type in (''VARCHAR2'',''VARCHAR'')',
'                          then ''varchar2_'' || c.char_length',
'                          when c.data_type like ''TIMESTAMP%''',
'                          then case',
'                                  when c.data_type like ''TIMESTAMP%WITH LOCAL TIME ZONE''',
'                                  then ''timestamp_ltz''',
'                                  when c.data_type like ''TIMESTAMP%WITH TIME ZONE''',
'                                  then ''timestamp_tz''',
'                                  else ''timestamp''                                  ',
'                               end',
'                          when to_char(c.data_length) is not null and c.data_type not in (''JSON'',''BOOLEAN'') ',
'                          then lower(c.data_type) ||''_''|| to_char(c.data_length)',
'                          else lower(c.data_type)',
'                     end as quicksql_data_type                ',
'                       from user_tab_columns c',
'                       where table_name = t.table_name',
'                       order by column_id) loop',
'                       p(c_spaces||replace(col.column_name,''$'',''_'')||c_spaces||col.quicksql_data_type);',
'            end loop;',
'            p(c_closed_curly_brace);',
'        end loop;',
'        return final_clob;',
'    end diagram_text_for_dualv;',
'    ',
'    function duality_view_exists(',
'        p_dualv_name in varchar2)',
'        return          boolean',
'    is',
'    begin',
'        for j in (select view_name',
'                    from user_json_duality_views',
'                   where view_name = p_dualv_name) loop',
'            return true;',
'        end loop;',
'        return false;',
'    end duality_view_exists;',
'',
'    function existing_duality_views_to_migrate',
'        return varchar2',
'    is',
'        l_list apex_t_varchar2;',
'    begin',
'        for j in q_json_tables_to_migrate loop',
'            if duality_view_exists(j.dualv_name) then',
'                apex_string.push(l_list,j.dualv_name);',
'            end if;',
'        end loop;',
'        return apex_string.join(l_list,'', '');',
'    end existing_duality_views_to_migrate;',
'',
'    procedure import_duality_views_to_migrate',
'    is',
'    begin',
'        for j in q_json_tables_to_migrate loop',
'            dbms_json_duality.import(',
'                table_name   => j.json_table_name,',
'                view_name    => j.dualv_name,',
'                err_log_name => j.dualv_name||''_ERR_LOG'');',
'        end loop;',
'    end import_duality_views_to_migrate;',
'',
'    procedure drop_tables_from_ddl(',
'        p_ddl clob)',
'    is',
'    begin',
'        for j in (select column_value as table_name from get_table_names_from_ddl(p_ddl)) loop',
'            execute immediate apex_string.format(''drop table if exists %s cascade constraints'', j.table_name);',
'        end loop;',
'    end drop_tables_from_ddl;',
'  ',
'end json_duality_companion;',
'/'))
);
