begin
wwv_flow_imp_shared.create_plugin(
 p_id=>wwv_flow_imp.id(2147404898964008188)
,p_plugin_type=>'REGION TYPE'
,p_name=>'MERMAID_DIAGRAM'
,p_display_name=>'Mermaid Diagram'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','MERMAID_DIAGRAM'),'')
,p_javascript_file_urls=>wwv_flow_string.join(wwv_flow_t_varchar2(
'https://cdnjs.cloudflare.com/ajax/libs/mermaid/10.9.1/mermaid#MIN#.js',
'https://cdn.jsdelivr.net/npm/svg-pan-zoom@3.5.0/dist/svg-pan-zoom#MIN#.js',
'#PLUGIN_FILES#script#MIN#.js'))
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'c_newline                 constant varchar2(2) := chr(13)||chr(10);',
'c_mermaid_erd             constant varchar2(9) := ''erDiagram'';',
'c_one_to_many             constant varchar2(8) := '' ||--|{ '';',
'c_open_curly_brace        constant varchar2(2) := '' {'';',
'c_closed_curly_brace      constant varchar2(1) := ''}'';',
'c_empty_relationship_name constant varchar2(2) :=  ''""'';',
'c_colon_separator         constant varchar2(3) :=  '' : '';',
'c_spaces                  constant varchar2(2) := ''  '';',
'',
'-- Concatentate a varchar2 to a clob as fast as possible.',
'-- Quicker than only a dbms_lob.append',
'-- Thank you to Mathieu Meeuwissen for the suggested approach',
'procedure vc_append(p_clob in out nocopy clob,',
'                    p_vc   in out nocopy varchar2,',
'                    p_text               varchar2)',
'is',
'begin',
'    p_vc := p_vc || p_text;',
'exception',
'    when value_error',
'    then',
'      if p_clob is null',
'      then',
'        p_clob := p_vc;',
'      else',
'        dbms_lob.append(p_clob,p_vc);',
'      end if;',
'      p_vc := p_text;',
'end vc_append;',
'',
'-- Format table title for Mermaid JS ER diagram',
'function format_title(',
'            p_table_name       in varchar2,',
'            p_table_prefix     in varchar2,',
'            p_pretty_tab_names in boolean) ',
'            return                varchar2',
'is',
'  l_table_name varchar2(200) := p_table_name;',
'begin',
'    if not p_pretty_tab_names then',
'        return lower(replace(replace(p_table_name,''$'',''_''),''#'',''_''));',
'    end if;',
'    if p_table_prefix is not null then',
'        if rtrim(p_table_prefix,''_'') = l_table_name then',
'            l_table_name := case ',
'                                when instr(l_table_name,''_'',-1) > 0 ',
'                                 and instr(l_table_name,''_'',-1) < length(l_table_name)',
'                                then substr(l_table_name,instr(l_table_name,''_'',-1)+1)',
'                                else l_table_name',
'                            end;',
'        else',
'            l_table_name := nvl(replace(l_table_name,p_table_prefix),l_table_name);',
'        end if;',
'    end if;',
'    l_table_name := initcap(replace(replace(replace(l_table_name,''_'','' ''),''$'',''_''),''#'',''_''));',
'    return ''"''||l_table_name||''"'';',
'end format_title;',
'',
'---------------------------------------------------------------------  ',
'-- Generate Mermaid JS ER diagram text for all tables matching prefix',
'--------------------------------------------------------------------- ',
'function diagram_text(p_table_prefix     in varchar2 := null, ',
'                      p_include_columns  in boolean := false, ',
'                      p_all_columns      in boolean := false,',
'                      p_pretty_tab_names in boolean := false  ) return clob is',
'    l_ret clob;',
'    l_ret_vc varchar2(32767);',
'    l_table_prefix varchar2(80) := rtrim(p_table_prefix,''_'');',
'    l_all_cols varchar2(1) := case when p_all_columns then ''Y'' end;',
'    procedure p(p_text varchar2) is',
'    begin',
'        vc_append(l_ret,l_ret_vc,p_text || c_newline);',
'    end;',
'    function final_clob return clob is',
'    begin',
'        return l_ret || l_ret_vc;',
'    end;',
'begin',
'    if l_table_prefix is not null then',
'        l_table_prefix := l_table_prefix||''_'';',
'    end if;',
'    p(c_mermaid_erd);',
'    -- add top level tables having no foreign key of their own',
'    for t in (select ut.table_name as lone_table_name',
'                from user_tables ut',
'               where (ut.table_name like replace(l_table_prefix,''_'',''\_'')||''%'' escape ''\'' or ut.table_name = rtrim(l_table_prefix,''_''))',
'               minus',
'              select distinct table_name from user_constraints uc',
'               where uc.constraint_type = ''R''',
'                 and (uc.table_name like replace(l_table_prefix,''_'',''\_'')||''%'' escape ''\'' ) or uc.table_name = rtrim(l_table_prefix,''_'')) loop',
'        p(format_title(t.lone_table_name,l_table_prefix,p_pretty_tab_names));',
'    end loop;',
'    -- add tables that include a foreign key',
'    for t in (select fk.table_name as many_table, pk.table_name as one_table',
'                from user_constraints fk',
'                left outer join user_constraints pk on pk.constraint_name = fk.r_constraint_name',
'                where fk.constraint_type = ''R''',
'                and (fk.table_name like replace(l_table_prefix,''_'',''\_'')||''%'' escape ''\'' or fk.table_name = rtrim(l_table_prefix,''_''))',
'                and (pk.table_name like replace(l_table_prefix,''_'',''\_'')||''%'' escape ''\'' or pk.table_name = rtrim(l_table_prefix,''_''))) loop',
'        p(format_title(t.one_table,l_table_prefix,p_pretty_tab_names)',
'            || c_one_to_many',
'            || format_title(t.many_table,l_table_prefix,p_pretty_tab_names)',
'            || c_colon_separator',
'            || c_empty_relationship_name);',
'    end loop;',
'    -- If we''re including columns, then generate the table descriptions',
'    -- to accompany the relationships above.',
'    if p_include_columns then',
'        for t in (select table_name',
'                   from user_tables',
'                   where (table_name like replace(l_table_prefix,''_'',''\_'')||''%'' escape ''\'' or table_name = rtrim(l_table_prefix,''_''))) loop',
'            p(format_title(t.table_name,l_table_prefix,p_pretty_tab_names)||c_open_curly_brace);',
'            for col in (select c.column_name,',
'                     case when to_char(c.data_length) is not null and (c.data_type = ''NUMBER'')',
'                          then ''num'' || case when c.data_precision is not null then ''_''||to_char(c.data_precision) || ''_'' || nvl(c.data_scale,0) end',
'                          when c.data_type in (''BLOB'',''CLOB'',''DATE'')',
'                          then lower(c.data_type)',
'                          when c.data_type in (''VARCHAR2'',''VARCHAR'')',
'                          then ''vc_'' || c.char_length',
'                          when c.data_type like ''TIMESTAMP%''',
'                          then case',
'                                  when c.data_type like ''TIMESTAMP%WITH LOCAL TIME ZONE''',
'                                  then ''tswltz''',
'                                  when c.data_type like ''TIMESTAMP%WITH TIME ZONE''',
'                                  then ''tswtz''',
'                                  else ''ts''                                  ',
'                               end',
'                          when to_char(c.data_length) is not null and c.data_type != ''JSON'' ',
'                          then lower(c.data_type) ||''_''|| to_char(c.data_length)',
'                          else lower(c.data_type)',
'                     end as quicksql_data_type                ',
'                       from user_tab_columns c',
'                       where table_name = t.table_name',
'                       and (l_all_cols = ''Y'' or c.column_name not in (''ID'', ''CREATED_BY'', ''CREATED'', ''UPDATED_BY'', ''UPDATED'',''ROW_VERSION''))',
'                       order by column_id) loop',
'                       p(c_spaces||lower(replace(col.column_name,''$'',''_''))||c_spaces||col.quicksql_data_type);',
'            end loop;',
'            p(c_closed_curly_brace);',
'        end loop;',
'    end if;',
'    return final_clob;',
'end diagram_text;',
'',
'function render (',
'    p_region              in apex_plugin.t_region,',
'    p_plugin              in apex_plugin.t_plugin,',
'    p_is_printer_friendly in boolean )',
'return apex_plugin.t_region_render_result',
'is',
'    c_elem_id constant varchar2(100) := p_region.static_id || ''_diagram'';',
'    l_return           apex_plugin.t_region_render_result;',
'    l_mermaid_text     clob;',
'    l_script_tag       clob;',
'begin',
'',
'    if apex_application.g_debug then',
'        apex_plugin_util.debug_region (',
'            p_plugin => p_plugin,',
'            p_region => p_region );',
'    end if;',
'',
'    if p_region.attribute_02 = ''Y'' then',
'        l_mermaid_text := diagram_text( p_table_prefix     => v(p_region.attribute_03),',
'                                        p_include_columns  => v(p_region.attribute_04) = ''Y'',',
'                                        p_all_columns      => v(p_region.attribute_05) = ''Y'',',
'                                        p_pretty_tab_names => v(p_region.attribute_06) = ''Y'');',
'    end if;',
'',
'    sys.htp.p( ''<div id="'' || apex_escape.html_attribute( c_elem_id ) || ''"></div>'' );',
'',
'    apex_json.initialize_clob_output;',
'',
'    apex_json.open_object;',
'    apex_json.write( ''elemId'', c_elem_id );',
'    apex_json.write( ''pageItem'', p_region.attribute_01 );',
'    apex_json.write( ''diagramText'', l_mermaid_text );',
'    apex_json.close_object;',
'',
'    dbms_lob.createtemporary(l_script_tag,true);',
'    dbms_lob.append(l_script_tag,''<script>window.addEventListener("load", () => {(function(){MermaidDiagram.init(''); ',
'    dbms_lob.append(l_script_tag,apex_json.get_clob_output( p_free => true ));',
'    dbms_lob.append(l_script_tag,'');})();});</script>'');',
'    apex_util.prn(l_script_tag,false);',
'    return l_return;',
'end render;'))
,p_default_escape_mode=>'HTML'
,p_api_version=>2
,p_render_function=>'render'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'1.0'
,p_files_version=>7
);
